    h1_Acceptance_MB1->SetBinError(pTBin_MB1, sqrt(pow(uncer_acc/yield_all, 2)+ pow( ( (yield_acc*uncer_all)/pow(yield_all, 2) ), 2) ) );

    yield_acc = h1_OmegaInAcc_MC_MB1->IntegralAndError(0, -1, uncer_acc);

